package wmq.fly.utils;

import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;

import org.springframework.web.multipart.MultipartFile;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

/**
 *   1. 前台传入base64图片解码为byte数组，再将byte数组java后台转为MultipartFile文件
 *   2.  将本地的视频或者图片先转换为byte数组，然后将byte数组转化为MultipartFile对象
 *
 */

public class ImageAndVideoToMultipartFile {
	
	public static void main(String[] args) {
		String imgStr ="Request URL: data:image/jpeg;base64,";
		MultipartFile mf = base64ToMultipart (imgStr);
	}
	
	
	/**
	 *  将base64格式的图片转换为Multipart
	 * @param base64
	 * @return
	 */
	public static MultipartFile base64ToMultipart(String base64) {
	    try {
	        String[] baseStrs = base64.split(",");

	        BASE64Decoder decoder = new BASE64Decoder();
	        byte[] b = new byte[0];
	        //将Base64格式的图片解码为byte数组
	        b = decoder.decodeBuffer(baseStrs[1]);

	        for(int i = 0; i < b.length; ++i) {
	            if (b[i] < 0) {
	                b[i] += 256;
	            }
	        }

	        return new ToMultipartFile(b, baseStrs[0]);
	    } catch (IOException e) {
	        e.printStackTrace();
	        return null;
	    }
	}

	/**
	 *  将本地的视频或者图片先转换为byte数组，然后将byte数组转化为MultipartFile对象
	 * @return
	 */
	public static ToMultipartFile toMileFile() {
		ToMultipartFile toMultipartFile = null;
		InputStream inputStream = null;
	    byte[] data = null;
	    try {
	        inputStream = new FileInputStream( "C:\\upload\\1.mp4");
	        data = new byte[inputStream.available()];
	        inputStream.read(data);
	        inputStream.close();
	        //将byte转换为MultipartFile格式的对象
	        toMultipartFile = new ToMultipartFile(data,null);
	        
	    } catch (IOException e) {
	        e.printStackTrace();
	    }
	    return toMultipartFile;
	}
	
	
	/**
	 * @Description: 将base64编码字符串转换为图片
	 * @Author: 
	 * @CreateTime: 
	 * @param imgStr base64编码字符串
	 * @param path 图片路径-具体到文件
	 * @return
	*/
	public static boolean generateImage(String imgStr) {
	if (imgStr == null)
	return false;
	BASE64Decoder decoder = new BASE64Decoder();
	try{
	// 解密
	byte[] b = decoder.decodeBuffer(imgStr);
	// 处理数据
	for (int i = 0; i < b.length; ++i) {
	if (b[i] < 0) {
	b[i] += 256;
	}
	}
	OutputStream out = new FileOutputStream("C:\\aimage\\new.jpg");
	out.write(b);
	out.flush();
	out.close();
	System.out.println("===================");
	return true;
	} catch (Exception e) {
	 return false;
	 }
	}

	
	/**
	 * @Description: 根据图片地址转换为base64编码字符串
	 * 
	 */
	public static String getImageStr() {
	    InputStream inputStream = null;
	    byte[] data = null;
	    try {
	        inputStream = new FileInputStream( "C:\\aimage\\test.png");
	        data = new byte[inputStream.available()];
	        inputStream.read(data);
	        inputStream.close();
	    } catch (IOException e) {
	        e.printStackTrace();
	    }
	    // 加密
	    BASE64Encoder encoder = new BASE64Encoder();
	    return encoder.encode(data);
	}
	
}

//将byte数组MultipartFile格式
class ToMultipartFile implements MultipartFile {

    private final byte[] imgContent;
    private final String header;

    public ToMultipartFile(byte[] imgContent, String header) {
        this.imgContent = imgContent;
        this.header = header.split(";")[0];
    }

    @Override
    public String getName() {
        // TODO - implementation depends on your requirements
        return System.currentTimeMillis() + Math.random() + "." + header.split("/")[1];
    }

    @Override
    public String getOriginalFilename() {
        // TODO - implementation depends on your requirements
        return System.currentTimeMillis() + (int)Math.random() * 10000 + "." + header.split("/")[1];
    }

    @Override
    public String getContentType() {
        // TODO - implementation depends on your requirements
        return header.split(":")[1];
    }

    @Override
    public boolean isEmpty() {
        return imgContent == null || imgContent.length == 0;
    }

    @Override
    public long getSize() {
        return imgContent.length;
    }

    @Override
    public byte[] getBytes() throws IOException {
        return imgContent;
    }

    @Override
    public InputStream getInputStream() throws IOException {
        return new ByteArrayInputStream(imgContent);
    }

    @Override
    public void transferTo(File dest) throws IOException, IllegalStateException {
        new FileOutputStream(dest).write(imgContent);
    }
}
